#include <iostream>using  namespace std;//#define SQUAER//#define TRIANGLE_1//#define TRUANGLE_2//#define SQUAER_1#define PLUS_MINUSvoid main(){    setlocale(LC_ALL, "");    int n;    cout << "Введите количество звездочек: "; cin >> n;#ifdef SQUAER    for (int i = 0; i < n; i++)    {        for (int i = 0; i < n; i++)        {            cout << "* ";        }        cout << endl;    }#endif SQUAER #ifdef TRIANGLE_1    for (int i = 0; i < n; i++)    {        for (int j = 0; j <= i; j++)        {            cout << "* ";        }        cout << endl;    }#endif TRIANGLE_1#ifdef TRUANGLE_2    for (int i = 0; i < n; i++)    {        for (int j = i; j < n; j++)        {            cout << "* ";        }        cout << endl;    }#endif TRUANGLE_2#ifdef PLUS_MINUS    for (int i = 0; i < n; i++)    {        for (int j = 0; j < n; j++)        {            //if ((i + j) % 2 == 0) cout << "+";            //if (i % 2 == j % 2) cout << "+";             (i % 2 == j % 2) ? cout << "+ " : cout << "- ";                    }        cout << endl;    }#endif PLUS_MINUS   }